let cnt:number = 0;
let currImage:number = 0;

export class MainScene extends Scene {

    @Resource.FontFromCssDescription({fontSize:10})
    private fnt:Font;

    public onReady():void {
        const tf:TextField = new TextField(this.game,this.fnt);
        tf.setWordBrake(WordBrake.PREDEFINED);
        tf.size.set(this.game.size);
        tf.textColor.setRGB(0,250,0);
        this.appendChild(tf);
        this.setInterval(()=>{
            tf.setText(frames[currImage]?.[cnt] ?? '');
            cnt = (++cnt)%frames[currImage].length;
        },100);
        this.mouseEventHandler.on(MOUSE_EVENTS.click, e=>{
            currImage = (++currImage)%frames.length;
        });
    }

}
